import React, { useState, useEffect } from 'react';
import './App.css';
import HomePage from './pages/HomePage';
import DashboardPage from './pages/DashboardPage';
import ReactionTimeTest from './tests/ReactionTimeTest';
import ChimpTest from './tests/ChimpTest';

const translations = {
  en: {
    brand: 'HUMAN BENCHMARK',
    dashboard: 'DASHBOARD',
    signup: 'SIGN UP',
    login: 'LOGIN',
    homeTitle: 'Human Benchmark',
    homeSubtitle: 'Measure your abilities with brain games and cognitive tests.',
    getStarted: 'Get Started',
    reactionTime: 'Reaction Time',
    reactionTimeDesc: 'How fast can you react?',
    chimpTest: 'Chimp Test',
    chimpTestDesc: 'Can you beat a chimpanzee?',
    reactionTimeTestTitle: 'Reaction Time Test',
    reactionTimeTestInstruction: 'When the red box turns green, click as quickly as you can.',
    clickToStart: 'Click anywhere to start.'
  },
  id: {
    brand: 'BENCHMARK MANUSIA',
    dashboard: 'DASBOR',
    signup: 'DAFTAR',
    login: 'MASUK',
    homeTitle: 'Tolok Ukur Manusia',
    homeSubtitle: 'Ukur kemampuan Anda dengan permainan otak dan tes kognitif.',
    getStarted: 'Mulai',
    reactionTime: 'Waktu Reaksi',
    reactionTimeDesc: 'Seberapa cepat Anda bisa bereaksi?',
    chimpTest: 'Tes Simpanse',
    chimpTestDesc: 'Bisakah Anda mengalahkan simpanse?',
    reactionTimeTestTitle: 'Tes Waktu Reaksi',
    reactionTimeTestInstruction: 'Saat kotak merah berubah menjadi hijau, klik secepat mungkin.',
    clickToStart: 'Klik di mana saja untuk memulai.'
  }
};

function MainApp() {
  const [currentPage, setCurrentPage] = useState('home');
  const [theme, setTheme] = useState('light');
  const [language, setLanguage] = useState('en');

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
      setTheme(savedTheme);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const toggleLanguage = () => {
    setLanguage(language === 'en' ? 'id' : 'en');
  };

  const t = translations[language];

  const renderPage = () => {
    switch(currentPage) {
      case 'home':
        return <HomePage setCurrentPage={setCurrentPage} t={t} />;
      case 'dashboard':
        return <DashboardPage t={t} />;
      case 'reactiontime':
        return <ReactionTimeTest t={t} />;
      case 'chimp':
        return <ChimpTest t={t} />;
      default:
        return <HomePage setCurrentPage={setCurrentPage} t={t} />;
    }
  };

  return (
    <div className={`app ${theme}`}>
      <nav className="nav">
        <div className="nav-left">
          <button 
            className="nav-brand" 
            onClick={() => setCurrentPage('home')}
            style={{ background: 'none', border: 'none', cursor: 'pointer' }}
          >
            {t.brand}
          </button>
          <button 
            className="nav-link"
            onClick={() => setCurrentPage('dashboard')}
            style={{ background: 'none', border: 'none', cursor: 'pointer' }}
          >
            {t.dashboard}
          </button>
        </div>
        <div className="nav-right">
          <button className="nav-button" type="button" onClick={toggleTheme}>
            {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
          </button>
          <button className="nav-button" type="button" onClick={toggleLanguage}>
            {language.toUpperCase()}
          </button>
          <button className="nav-button" type="button">
            {t.signup}
          </button>
          <button className="nav-button" type="button">
            {t.login}
          </button>
        </div>
      </nav>
      <main className="main-content">
        {renderPage()}
      </main>
    </div>
  );
}

export default MainApp;
